import axios from 'axios'
import React, {useState, useEffect} from 'react'
import DisplayDishes from './DisplayDishes'

const AddDish = () => {
  const [dish, setDish] = useState()

  const submitForm = (name, dishId) => {
    // fetch('https://localhost:3000/dishes', {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //     "Accept": "application/json"
    //   },  
    //   body: JSON.stringify(dish)
    // })
    // .then(response => response.json())
    // .then(dish => {
    //   console.log(dish)
    // }, [])

    axios({        
      headers: { 
      'content-type': 'application/json'
      },
      method: 'post',
      url:`https://localhost:3000/dishes/${dishId}`,
      params: {
        name
      }
    })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
  } 

  useEffect(() => {
    submitForm()
  });

  const handleOnSubmit = (event) => {
    event.preventDefault()
    setDish('')
  }

  const handleOnChange = (event) => {
    setDish(event.target.value)
    console.log(event.target.value)

  }

  return (
    <div>
      <form className='add-dish-form' onSubmit={handleOnSubmit}> 
        <label>DISH NAME: </label>
        <input type='text' name='name' onChange={handleOnChange} value={dish} placeholder='dish...'/>
        <input type='submit' value='Submit'/> 
       </form>
    </div>
  )
}

export default AddDish
