import React, {useState} from 'react'
import './App.css';

const App = () => {
  const [dish, setDish] = useState([
    {name: 'Injera'},
    {name: 'Akara'},
    {name: 'Bandeja Paisa'},
    {name: 'Tandoori Chicken'}
  ])

  const newDish = (name) => {
    const newDish = [...dish, { name }]
    setDish(newDish)
  }

  const removeDish = (index) => {
    const newDish = [...dish]
    newDish.splice(index, 1)
    setDish(newDish)
  }

  return (
    <div className="App">
      {console.log(dish)}
      <div>
        {/*eslint-disable-next-line array-callback-return*/}
        {dish.map((dish, index) => <Dish key={index} 
          index={index} 
          dish={dish} 
          removeDish={removeDish} 
        />
        )}
        <DishForm addNewDish={newDish}/>
      </div>
    </div>
  );
}

const DishForm = ({ addNewDish }) => {
  const [newDish, setNewDish] = useState('')

  const handleOnChange = (event) => {
    setNewDish(event.target.value)
  }

  const handleOnSubmit = (event) => {
    event.preventDefault()
    if(!newDish) return
      addNewDish(newDish)
      setNewDish('')

  }

  return (
    <div>
      <form onSubmit={handleOnSubmit}>
        <input type='text' onChange={handleOnChange} value={newDish} />
        <input type='submit' value='Submit' />
      </form>
    </div>
  )
}

const Dish = ({ dish, index, removeDish }) => {

  return (
    <div>
      <ul className={'dish-list'}>
        <li>
        {dish.name}
        <button onClick={() => removeDish(index)}>x</button>
        </li>
      </ul>
    </div>
  );

}

export default App;
