import React, { Component } from 'react'

export default class AddDish extends Component {
  constructor() {
    super()
    this.state = {
      name: ''
    }
  }

  componentDidMount(dish){
    fetch('http://localhost:3000/dishes', {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
      },  
      body: JSON.stringify(dish)
    })
    .then(response => response.json())
    .then(dish => console.log(dish))
  }

  handleOnSubmit = (event) => {
    event.preventDefault()

  }

  handleOnChange = (event) => {
    this.setState({[event.target.value]: event.target.value})
    console.log(event.target.value)

  }

  render() {
    return (
      <div>
        <form className='add-dish-form' onSubmit={this.handleOnSubmit}> 
        <label>DISH NAME: </label>
        <input type='text' onChange={this.handleOnChange} value={this.state.name} />
        <input type='submit' value='Submit'/> 
       </form>
      </div>
    )
  }
}
