import axios from 'axios'
import React, {useState, useEffect} from 'react'


const Dish = () => {
  const [dishName, setDishName] = useState('')

  const submitForm = (name) => {
    // fetch('https://localhost:3000/dishes', {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //     "Accept": "application/json"
    //   },  
    //   body: JSON.stringify(dish)
    // })
    // .then(response => response.json())
    // .then(dish => {
    //   console.log(dish)
    // }, [])

    axios.post({        
      headers: { 
      'content-type': 'application/json'
      },
      method: 'post',
      url:'https://localhost:3000/dishes',

    })
    .then(function (response) {
      console.log(response);
    })
    // .catch(function (error) {
    //   console.log(error);
    // });
  } 

  useEffect(() => {
    submitForm()
  });

  const handleOnSubmit = (event) => {
    event.preventDefault()
    setDishName('')
  }

  const handleOnChange = (event) => {
    setDishName(event.target.value)
    console.log(event.target.value)

  }

  return (
    <div>
      <form className='add-dish-form' onSubmit={handleOnSubmit}>
        <label>DISH NAME: </label>
        <input type='text' name='name' onChange={handleOnChange} value={dishName} placeholder='dish...'/>
        <input type='submit' value='Submit'/>
      </form>
    </div>
  )
}

export default Dish
